import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Home() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/data')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);


  // Gửi thông tin name về backend khi bấm vào đường link
  // const handleClick = (name) => {
  //   console.log('Clicked on:', name);

  //   // axios.post('http://localhost:5002/namePage', { name })
  //   //   .then(response => {
  //   //     // Xử lý dữ liệu trả về từ backend (nếu cần)
  //   //   })
  //   //   .catch(error => {
  //   //     console.error('Error sending data:', error);
  //   //   });
  // };

  const [data2, setData2] = useState([]);
  const handleClick = (name) => {
    axios.get('http://localhost:5001/api/data')
      .then(response => {
        setData2(response.data2);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };



  return (
    <div>
      <h1>Chào mừng đến với SciPlay</h1>
      <h2>Hôm nay bạn muốn học gì?</h2>

      <ul>
        <li>
          <Link to="/homepage">Trang chủ</Link>
        </li>
        <li>
          <Link to="/homepage">Học</Link>
        </li>
        <li>
          <Link to="/homepage">Thử Thách</Link>
        </li>
        <li>
          <Link to="/homepage">Xếp hạng</Link>
        </li>
        <li>
          <Link to="/homepage">Diễn đàn</Link>
        </li>
        <li>
          <Link to="/homepage">Cửa hàng</Link>
        </li>
      </ul>

      <br />
      <h3>Các khóa đang học</h3>
      <ul>
        {data.map(item => (
          <li key={item.id}>
            <a href="#" onClick={() => {handleClick(item.name)}}>
              {item.name}: {item.description}
            </a>
          </li>
        ))}
      </ul>

      <ul>
        {data2.map(item => (
          <li key={item.id}>
            <a href="#" onClick={() => {handleClick(item.name)}}>
              {item.name}: {item.description}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
