// import React from 'react';

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';

function Lesson({ history }) {

  const [data, setData] = useState([]);
  const {id} = useParams();
  console.log("Lesson id: ", id);

  useEffect(() => {
    axios.get(`http://localhost:5001/namePage?id=${id}`)
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);



  return (

    <div>
      {/* <h2>{name}</h2> */}
      <h2>Lesson Page</h2>
      <Link to="/">Back home</Link>
      <br />
      <br />
      {data.map(item => (
        <div key={item.name}>
          <h3>{item.name}</h3>
          <p>{item.duration} minutes</p>
          <h4>Content:</h4>
          {/* Lặp qua các phần tử trong mảng 'content' */}
          {item.content.map((contentItem, index) => (
            <div key={index}>
              {/* Lặp qua các phần tử trong mảng 'lines' */}
              {contentItem.lines.map((line, lineIndex) => (
                <p key={lineIndex}>{line}</p>
              ))}
            </div>
          ))}
        </div>
      ))}

    </div>
  );
}

export default Lesson;
